mapper.select:
SELECT *
FROM (
    SELECT ROWNUM AS rnum, A.*
    FROM (
        SELECT *
        FROM TB_NOTICE_COMMENT
        WHERE NOTICE_ID = #{noticeId}
          AND NOTICE_COMMENT_IS_DEL = 'N'
        ORDER BY NOTICE_COMMENT_CREATED_AT DESC
    ) A
)
WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT *
FROM (
    SELECT ROWNUM AS rnum, A.*
    FROM (
        SELECT *
        FROM TB_NOTICE_COMMENT


                NOTICE_COMMENT_IS_DEL = 'N'

            AND NOTICE_ID = #{noticeId}

        ORDER BY NOTICE_COMMENT_CREATED_AT DESC
    ) A
)
WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(*) FROM TB_NOTICE_COMMENT
NOTICE_COMMENT_IS_DEL = 'N'
AND NOTICE_ID = #{noticeId}

mapper.update:
UPDATE TB_NOTICE_COMMENT
SET NOTICE_COMMENT_IS_DEL = 'Y'
WHERE NOTICE_COMMENT_ID = #{noticeCommentId}
  AND MEMBER_NO = #{memberNo}
  AND NOTICE_COMMENT_IS_DEL = 'N'
